const services=document.querySelector(".services"),servicesLinks=services.querySelectorAll(".services__link"),servicesTabs=services.querySelectorAll(".services__tab"),deleteActiveClass=()=>{servicesLinks.forEach(e=>{e.classList.remove("services__link--current")}),servicesTabs.forEach(e=>{e.classList.remove("services__tab--current")})};servicesLinks.forEach((s,r)=>{s.addEventListener("click",e=>{e.preventDefault(),deleteActiveClass(),s.classList.add("services__link--current"),servicesTabs[r].classList.add("services__tab--current")})});